            /// Gets or Sets the number of Assets 
            /// </summary>
            public int AssetCount { get; set; }

            /// <summary>
            /// Gets or Sets the Start Address
            /// </summary>
            public long StartAddress { get; set; }

            /// <summary>
            /// Gets or Sets the End Address
            /// </summary>
            public long EndAddress { get { return StartAddress + (AssetCount * AssetSize); } set => throw new NotImplementedException(); }

            /// <summary>
            /// Gets the Name of this Pool
            /// </summary>
            public string Name => "weapon";

            /// <summary>
            /// Gets the Setting Group for this Pool
            /// </summary>
            public string SettingGroup => "Weapon";

            /// <summary>
            /// Gets the Index of this Pool
            /// </summary>
            public int Index => (int)AssetPool.weapon;

            /// <summary>
            /// Loads Assets from this Asset Pool
            /// </summary>
            public List<Asset> Load(HydraInstance instance)
            {
                var results = new List<Asset>();

                var poolInfo = instance.Reader.ReadStruct<AssetPoolInfo>(instance.Game.AssetPoolsAddress + (Index * 0x20));

                StartAddress = poolInfo.PoolPointer;
                AssetSize = poolInfo.AssetSize;
                AssetCount = poolInfo.PoolSize;

                var headers = instance.Reader.ReadArrayUnsafe<WeaponAssetHeader>(StartAddress, AssetCount);

                for(int i = 0; i < headers.Length; i++)
                {
                    var header = headers[i];

                    if (IsNullAsset(header.NamePointer))
                        continue;

                    var address     = StartAddress + (i * AssetSize);

                    results.Add(new Asset()
                    {
                        Name        = instance.Reader.ReadNullTerminatedString(header.NamePointer),
                        Type        = Name,
                        Zone        = ((BlackOps3)instance.Game).ZoneNames[address],
                        Information = "N/A",
                        Status      = "Loaded",
                        Data        = address,
                        LoadMethod  = ExportAsset,
                    });
                }

                return results;
            }

            /// <summary>
            /// Exports the given asset from this pool
            /// </summary>
            public void ExportAsset(Asset asset, HydraInstance instance)
            {
                var buffer = instance.Reader.ReadBytes((long)asset.Data, AssetSize);

                // Buffer of entire size Linker allocates
                var assetBuffer = new byte[0x2970];

                // Start by stitching the asset together so we can deal with a large buffer,
                // linker generates it in this fashion, but once loaded into memory, it's split into
                // sections with pointers

                // XAsset Buffer
                Buffer.BlockCopy(buffer, 0, assetBuffer, 0, buffer.Length);
                // Weapon Settings
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 24),          5161),          0, assetBuffer,         768,            5161);
                // Attachments (Not needed, just here for clarity and if ever needed, set by the AUs)
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 56),          512),           0, assetBuffer,         6736,           512);
                // Attachment Uniques (Not needed, just here for clarity and if ever needed, set Attachment Unique Base in APE)
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 56),          1024),          0, assetBuffer,         7248,           1024);
                // Animations
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 72),          1576),          0, assetBuffer,         8272,           1576);
                // Hide Tags
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 80),          128),           0, assetBuffer,         9848,           128);
                // Attachment Data (View/World Model Model Pointers/Tags)
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 88),          48),            0, assetBuffer,         9976,           48);
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 96),          48),            0, assetBuffer,         10016,          48);
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 104),         48),            0, assetBuffer,         10056,          48);
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(buffer, 112),         48),            0, assetBuffer,         10096,          48);
                // Attachment Cosmetic Variants
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(assetBuffer, 768 + 96),   352),       0, assetBuffer,         6384,           352);
                // Projectile Settings (Parallel/Perpendicular)
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(assetBuffer, 768 + 4504), 160),       0, assetBuffer,         10136,          160);
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(assetBuffer, 768 + 4512), 160),       0, assetBuffer,         10296,          160);
                // Damage Multipliers
                Buffer.BlockCopy(instance.Reader.ReadBytes(BitConverter.ToInt64(assetBuffer, 768 + 5040), 84),        0, assetBuffer,         10456,          84);

                // Now pass to the asset converter
                var result = ConvertAssetBufferToGDTAsset(assetBuffer, WeaponOffsets, instance, HandleSpecialWeaponsFields);

                var weaponType = (string)result["weaponType"];
                var isDualWield = (byte)result["dualWield"] == 1;

                // This is a workaround for the fact that the gdf type is discarded 
                // and linker only references it for gdtdb look ups

                if(isDualWield)
                {
                    // Check for right hand
                    if ((string)result["viewmodelTag"] == "tag_weapon_right")
                        result.Type = (weaponType == "projectile" ? "projectile" : "bullet") + "weapon";
                    else
                        result.Type = "dualwield" + (weaponType == "projectile" ? "projectile" : "bullet") + "weapon";

                }
                // For turrets, check minHorTurnSpeed, it has to be at least 1
                else if ((float)result["minHorTurnSpeed"] >= 1.0f)
                {
                    result.Type = "turretweapon";
                }
                // For bullet/gas/projectile, check maxDist, it has to be at least 1
                else if ((float)result["maxDist"] >= 1.0f)
                {
                    if ((string)result["weaponType"] == "projectile" || (string)result["weaponType"] == "bomb")
                        result.Type = "projectileweapon";
                    else if ((string)result["weaponType"] == "gas")
                        result.Type = "gasweapon";
                    else
                        result.Type = "bulletweapon";
                }
                else
                {
                    result.Type = weaponType + "weapon";
                }

                result.Name = asset.Name;
                instance.AddGDTAsset(result, result.Type, result.Name);
            }

            /// <summary>
            /// Handles Weapon Specific Settings
            /// </summary>
            private static object HandleSpecialWeaponsFields(GameDataTable.Asset asset, byte[] assetBuffer, int offset, int type, HydraInstance instance)
            {
                switch (type)
                {
                    case 0x33:
                        return WeaponTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x34:
                        return WeaponClasses[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x35:
                        return WeaponReticles[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x36:
                        return WeaponPenetrationTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x37:
                        return WeaponImpactTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x38:
                        return WeaponDamageTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x39:
                        return WeaponStances[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3A:
                        return WeaponProjectileExplosionTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3B:
                        return WeaponOffhandClasses[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3C:
                        return WeaponOffhandSlots[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3D:
                        return WeaponPlayerAnimTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3E:
                        return WeaponReticleTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x3F:
                        return WeaponGuidedMissileTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x40:
                        return WeaponLockOnTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x41:
                        return WeaponStickTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x42:
                        return WeaponStickOrientations[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x43:
                        return WeaponRotationTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x44:
                        return WeaponADSOverlays[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x45:
                        return WeaponInventoryTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x46:
                        return WeaponFireTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x47:
                        return WeaponClipTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x48:
                        return WeaponCounterClipTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x49:
                    case 0x4A:
                    case 0x4B:
                    case 0x4C:
                    case 0x4D:
                        return WeaponIconRatios[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x4F:
                        return WeaponBarrelTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x50:
                        {
                            string hideTagsString = "";
                            for (int i = 0; i < 0x20; i++)
                                hideTagsString += instance.Game.GetString(BitConverter.ToInt32(assetBuffer, 0x2678 + (i * 4)), instance) + "\\r\\n";
                            return hideTagsString;
                        }
                    case 0x51:
                        return instance.Game.GetAssetName(BitConverter.ToInt64(assetBuffer, offset), instance);
                    case 0x52:
                        return instance.Game.GetString(BitConverter.ToInt32(assetBuffer, offset), instance);
                    case 0x56:
                        return WeaponGadgetTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x57:
                        return WeaponGadgetActivateModes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x58:
                        return WeaponGadgetHoldModes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x59:
                        return WeaponGadgetPowerUseTypes[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x5A:
                    case 0x5B:
                        return WeaponPulseShareType[BitConverter.ToInt32(assetBuffer, offset)];
                    case 0x5C:
                        return WeaponPulseType[BitConverter.ToInt32(assetBuffer, offset)];
                    default:
                        {
                            return null;
                        }
                }
            }

            /// <summary>
            /// Checks if the given asset is a null slot
            /// </summary>
            public bool IsNullAsset(long nameAddress)
            {
                return nameAddress >= StartAddress && nameAddress <= EndAddress || nameAddress == 0;
            }
